mapper.select:
select count(task.line_id) as count,line.name as lineName from tb_inspection_task as task left join tb_inspection_line as line on line.id=task.`line_id` left join tb_turn as turn on turn.id=task.turn_id left join sys_dept as dept on dept.dept_id=line.dept_id left join tb_inspection_period as period on period.line_id=line.id where task.is_inspected=0 and task.inspection_span_end_date >= '${inspectStartDate}' and task.inspection_span_end_date < '${inspectEndDate}'
and dept.dept_id in
#{item}
and line.id=#{lineId}
group by task.line_id

select dept.name as deptName,line.name as lineName,case line.period_type when 1 then '天' when 2 then '日' when 3 then '月' end as periodName,turn.id as turnId,turn.name as turnName, case period.turn_finish_mod when 0 then '全体完成' when 1 then '班组完成' when 2 then '个人完成' end as turnFinishMod, task.turn_start_time as turnStartTime,task.turn_end_time as turnEndTime from tb_inspection_task as task left join tb_inspection_line as line on line.id=task.`line_id` left join tb_turn as turn on turn.id=task.turn_id left join sys_dept as dept on dept.dept_id=line.dept_id left join tb_inspection_period as period on period.line_id=line.id where task.is_inspected=0 and task.inspection_span_end_date >= '${inspectStartDate}' and inspection_span_end_date < '${inspectEndDate}'
and dept.dept_id in
#{item}
and line.id=#{lineId}
order by turn_start_time desc

select dept.name as deptName,line.name as lineName,turn.id as turnId,turn.name as turnName,task.inspection_span_end_date, count(task.line_id) as inspectCount ,sum(case task.is_inspected when 1 then 1 else 0 end) as inspectedCount, count(task.line_id) - sum(case task.is_inspected when 1 then 1 else 0 end) as mustInspectCount, concat(round((count(task.line_id) - sum(case task.is_inspected when 1 then 1 else 0 end))/count(task.line_id)*100,2),'%') as inspectRate, '${inspectStartDate}' as inspectStartDate,'${inspectEndDate}' as inspectEndDate from tb_inspection_task as task left join tb_inspection_line as line on line.id=task.`line_id` left join tb_turn as turn on turn.id=task.turn_id left join sys_dept as dept on dept.dept_id=line.dept_id left join tb_inspection_period as period on period.line_id=line.id where task.inspection_span_end_date >= '${inspectStartDate}' and task.inspection_span_end_date < '${inspectEndDate}'
and dept.dept_id in
#{item}
and line.id=#{lineId}
group by line.id

select dept.name as deptName,line.name as lineName,turn.id as turnId,turn.name as turnName, count(task.line_id) as inspectCount ,sum(case task.is_inspected when 1 then 1 else 0 end) as inspectedCount, '${inspectStartDate}' as inspectionStartDate,'${inspectEndDate}' as inspectionEndDate from tb_inspection_task as task left join tb_inspection_line as line on line.id=task.`line_id` left join tb_turn as turn on turn.id=task.turn_id left join sys_dept as dept on dept.dept_id=line.dept_id left join tb_inspection_period as period on period.line_id=line.id where inspection_span_end_date >= '${inspectStartDate}' and inspection_span_end_date < '${inspectEndDate}'
and dept.dept_id in
#{item}
and line.id=#{lineId}
group by line.id,turn.id
